import pandas as pd
import os
import matplotlib.pyplot as plt
from prompt_systematic_review.config_data import DataFolderPath

"""Graph the model counts generated by scripts/count_models.py"""


def graph_models(inputFile="model_citation_counts.csv"):
    csv_file = os.path.join(DataFolderPath, inputFile)
    df = pd.read_csv(csv_file)

    # sort by alphabetical order
    top_20 = df.sort_values(by="count", ascending=False)

    plt.figure(figsize=(10, 6))
    plt.bar(top_20["model_name"], top_20["count"], color="blue")
    plt.xlabel("Model Name")
    plt.ylabel("Count")
    plt.title("Counts of Model Mentions in Dataset")
    plt.xticks(rotation=45, ha="right")
    plt.tight_layout()

    plt.savefig(
        os.path.join(
            DataFolderPath, "experiments_output" + os.sep + "graph_models_output.png"
        )
    )


class Experiment:
    def run():
        graph_models()
